import React, { Component } from 'react';
import './App.css';

class Header extends Component {
  render() {
    return (

      <nav class="nav">
        <div class="logo-container">
        </div>

        <ul class="list-items">
          <li class="list">
            <a href="#" class="link"> Notes </a>
          </li>
          <li class="list">
            <a href="#" class="link"> Lists </a>
          </li>

          <li class="list">
            <a href="#" class="link"> Recordings </a>
          </li>
          <li class="list">
            <a href="index.html" class="link"> </a>

          </li>
        </ul>
      </nav>
    );
  }
}

class Footer extends Component{

render(){
return(
<nav class="nav">
        <div class="logo-container">       
        </div>

        <ul class="list-items">
          <li class="list">
            <a href="#" class="link"> Home </a>
          </li>
          <li class="list">
            <a href="#" class="link"> Add </a>
          </li>

          <li class="list">
            <a href="#" class="link"> Profile </a>
          </li>
          <li class="list">
            <a href="index.html" class="link"> </a>

          </li>
        </ul>
      </nav>
);
}


}



class Search extends Component {

render(){
return(
<center>
<div class="topnav">
<input type="text" placeholder="Search.." />
</div>
</center>
);
}
}

class Notes extends Component{

  render() {
    const boxStyle = {
      width: '200px',
      height: '200px',
      margin: '10px',
      padding: '10px',
      borderRadius: '25px',
    };

    return (
      <div>

        <style>
          {`
            body {
              margin: 10px;
              font-family: "Trebuchet MS", Arial, Helvetica, sans-serif;
            }

            #box1{
              width:200px;
              height:200px;
              margin:10px;
              padding:10px;
              borderRadius:25px;
            }
            #box2{
              width:200px;
              height:200px;
              margin:10px;
              padding:10px;
              borderRadius: '25px'
            }

             #box3{
              width:200px;
              height:200px;
              margin:10px;
              padding:10px;
            }

             #box4 {
